choose.when:
		SELECT vid FROM(
		SELECT r1.type,v1.vid
		,(SELECT COUNT(*) FROM iptv_res_ver v4,iptv_res r4,iptv_res r5,iptv_res_ver v5
			WHERE v4.link_prid=r1.rid AND v4.link_crid=r5.rid AND r5.type='song' AND r5.media_ok=1 AND r5.global_disable=0 AND r5.test_vid=v5.vid AND v5.version_disable=0
			AND v4.rid=r4.rid AND r4.test_vid=v4.vid AND r4.type='link' AND r4.global_disable=0 AND v4.version_disable=0
			) song_num
		FROM iptv_res r1
		INNER JOIN iptv_res_ver v1 ON (v1.rid=r1.rid AND r1.test_vid=v1.vid AND v1.version_disable=0)
		WHERE r1.global_disable=0 AND r1.media_ok=1 AND r1.type=#{type}

            and v1.vid not in #{item}

        HAVING (TYPE!='singer') OR (TYPE='singer' AND song_num!=0)
		ORDER BY RAND()
		LIMIT #{size}
		) result

